import {useContext} from "react";

import Editor from "./Editor";

import {Box,styled} from '@mui/material';

import { DataContext } from "../context/DataProvider";
const Container=styled(Box)`
     display:flex;
     background-color:#060606;
     height:58vh;
`


const Code=() =>{
    const { html , css , js ,setHtml , setCss , setJs }= useContext(DataContext);
    return (
        <Container>
        <Editor
          heading="HTML"
          language='xml'
          icon="/"
          color="#FF3C41"
          value={html}
          onChange={setHtml}
        />
        <Editor
          heading="CSS"
          language="css"
          icon="*"
          color="#0EBEFF"
          value={css}
          onChange={setCss}
        />
        <Editor
          heading="JavaScript"
          language="javascript"
          icon="()"
          color="#FCD000"
          value={js}
          onChange={setJs}
        />
        </Container>
    )
}
export default Code;
